import { Box, useTheme } from '@mui/material';

/** Denotes sizes for different items, mainly Images (Logos or Avatars for example) */
export type Size = 'tiny' | 'small' | 'medium' | 'large' | 'extra';

export type CustomLogoSize =
  | {
      type: 'DETAILED_DIMENSIONS';
      dimensions: {
        brandLogoWidth: number;
        customLogoPadding: number;
        badgeSize: number;
        badgeFontSize: number;
      };
    }
  | { type: 'CONCISE_DIMENSIONS'; width: number };

export type LogoSize = Size | CustomLogoSize;

interface Props {
  displayWriting: 'HIDDEN' | 'FRAMED';
  size: LogoSize;
}

