const getLogoFrameWidth = (size: LogoSize) => {
  if (typeof size != 'string' && size.type === 'DETAILED_DIMENSIONS') {
    return size.dimensions.brandLogoWidth;
  }

  if (typeof size != 'string' && size.type === 'CONCISE_DIMENSIONS') {
    return size.width;
  }

  switch (size) {
    case 'large':
      return 17;
    case 'medium':
      return 10;
    case 'small':
      return 4;
    case 'tiny':
      return 3;
    default:
      return 10;
  }
};

